-- ----------------------------
-- Table structure for causes
-- ----------------------------
DROP TABLE IF EXISTS `causes`;
CREATE TABLE `causes`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '项目id',
  `title` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '项目标题',
  `summary` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '项目摘要',
  `author` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '项目发布者',
  `task` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '项目任务目标',
  `background` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '项目背景',
  `raised` int(11) NULL DEFAULT 0 COMMENT '已筹集资金',
  `goal` int(11) NULL DEFAULT NULL COMMENT '目标资金',
  `type` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1' COMMENT '项目分类(1普通公益2特色公益3热门活动)',
  `bigPic` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '项目展示大图路径',
  `smallPic` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '项目展示小图路径',
  `create_time` datetime NULL DEFAULT NULL COMMENT '项目发布时间',
  `del_flag` int(11) NULL DEFAULT 0 COMMENT '删除标志(0表示未删除1表示已删除)',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 16 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '慈善项目表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of causes
-- ----------------------------
INSERT INTO `causes` VALUES (1, 'Small hands are held by tiny acts of kindness, and young plants are nourished by boundless love.', 'The public welfare Fund Management Committee went to poor counties to carry out \"Internet + education\" poverty alleviation and public welfare education activities. And is ready to raise 70,000 yuan to poor counties to donate tablet computers, teaching software cards and other educational information equipment. It aims to help rural children in poor counties, left-behind children and children of migrant workers improve their learning conditions and stimulate their interest in learning.', 'CHILDREN\'S CHARITY', 'Help rural children in poor counties, left-behind children and children of migrant workers to improve their learning conditions and stimulate their interest in learning. The recipient schools should really manage and make good use of these equipment to make the best use of them to serve our education and teaching work; The teachers should redouble their efforts to continuously improve their education level and management level, and strive to build a good school that satisfies the society, the people and the students, so as to live up to the support and love from all walks of life.', 'According to reports, in order to implement the relevant requirements of the rural Labor Party Central Committee for the democratic supervision of poverty alleviation in poor provinces, implement the strategic deployment of the central Committee on education informatization and the spirit of the second National Education Informatization Work Conference, actively participate in the national precision poverty alleviation plan of the Central Committee of the Rural Labor Party, the public welfare Fund Management Committee jointly with the Department of Industry and Information Technology of poor provinces, In poor provinces, we jointly carried out \"Internet + education\" public welfare activities to promote education informatization, supported the development of basic education in 100 poor counties (districts and cities) with relatively backward education, donated information materials, and promoted the deep integration of information technology and education and teaching. Poor counties are one of them.', 105, 75000, '3', 'http://127.0.0.1:8081/TheCharity/images/1.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-1.jpg', '2022-04-06 08:42:09', 0);
INSERT INTO `causes` VALUES (2, 'Give life a chance, give your children a future', 'Sharing the Blue Sky • Contributing to the Long \"Love Desk and chair donation charity activity entered the poor children\'s school in India, and donated 200 sets of love desk and chair to poor children from poor families in poor children\'s schools in India.', 'AI YOU FOUNDATION', 'We are aiming at some poor families of children, their needs for desks and chairs, arranged for them to use, hoping that these desks and chairs can improve their learning conditions, can let them develop good writing habits. It is helpful for their academic progress and healthy growth. The Foundation Federation of Trade unions is ready to subsidize 90,000 yuan to buy more than 5,000 sets of love desks and chairs, and is ready to give all to the poor areas of cities and counties and the hands of children in distress in the old revolutionary areas.', 'The Foundation Federation of Trade Unions will continue to do practical work for schools and children, care for and help children in need to solve the problem of desks and chairs, create and strive for more and better learning and education resources for them, constantly improve learning and education conditions and environment, encourage children to redouble their efforts, study hard, and serve the motherland and society when they grow up, and build their hometown.', 0, 90000, '1', 'http://127.0.0.1:8081/TheCharity/images/2.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-2.jpg', '2022-04-17 15:27:52', 0);
INSERT INTO `causes` VALUES (3, 'Launch the \"Most Beautiful Growth Road\" children nutrition public welfare program to help primary school students grow healthily', '\"The Most Beautiful Growth Road\" children\'s nutrition program was officially launched. A public welfare program for children\'s nutrition was launched to cover five rural primary schools, and since then more than 1,700 primary school students have received nutritious milk at school.', 'CHILDREN\'S CHARITY', '\"The Most Beautiful Growth Road\" children\'s nutrition program was officially launched. A public welfare program for children\'s nutrition was launched to cover five rural primary schools, and since then more than 1,700 primary school students have received nutritious milk at school.', 'According to the \"Chinese Dietary Guidelines for school-age Children\" published by the Chinese Nutrition Society, the \"a2 nutrition station\" provides children with 300 ml of milk per day, and is equipped with milk cups and insulated buckets for children. At present, the first batch of milk powder has all arrived and put into use. Nong Xingliang, principal of XX County Central School, said that the next step will be to do a good job in milk distribution management and physical monitoring, so that love milk powder can truly become a catalyst for the healthy growth of students.', 0, 60000, '2', 'http://127.0.0.1:8081/TheCharity/images/3.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-3.jpg', '2022-04-20 15:30:07', 0);
INSERT INTO `causes` VALUES (4, 'Centennial Life Insurance Shaanxi Branch volunteers to care for autistic children public welfare activities', 'Children with autism are also called \"children from the stars.\" For they shine in the distance like stars in the sky, immersed in their own world, unwilling to communicate with others. No matter who they are, they are as far away as stars.', 'Children\'s Foundation of China', 'Only by providing a better social atmosphere for autistic children and giving them more patience, support, respect and companionship can we help them grow up healthily. Caring for autistic children still needs more efforts from us and people in the society.', 'According to the Autism in America 2020 Report, based on 2016 data, one in every 54 children in the United States will have autism. There are 11 million autistic people in China, including more than 2 million autistic children. Children with autism are also called \"children from the stars.\" For they shine in the distance like stars in the sky, immersed in their own world, unwilling to communicate with others. No matter who they are, they are as far away as stars. The volunteers of Centennial Life xx Company came to the \"Lala hand Special Education Center\" in xx City to visit the children with \"autism\". On the day of the activity, it was the birthday of an autistic child \"Ben Ben\". The volunteers of Centennial Shaanxi Fen brought a beautiful cake for \"Ben Ben\". Together for the \"Ben Ben\" children to celebrate the birthday, the activity site, teachers, volunteers, other children sang songs, for the \"Ben Ben\" children sent the best birthday wishes, the activity site was full of laughter, the children spent a good afternoon accompanied by teachers and volunteers.', 0, 45000, '1', 'http://127.0.0.1:8081/TheCharity/images/4.jpg', 'http://127.0.0.1:8081/TheCharity/images/event-1.jpg', '2022-04-19 15:33:43', 0);
INSERT INTO `causes` VALUES (5, 'Nanosomia Care activities go to poor villages to popularize nanosomia knowledge', 'A series of public welfare activities for children\'s growth and development were launched in poor villages. The event was co-sponsored by the Red Cross Foundation of China and the Second Affiliated Hospital of XX University. The activity invited experts from the Department of Pediatrics and child care of the hospital to give on-site lectures, and carried out a free clinic for children\'s growth and development, conducted bone age assessment for children with short height, and helped children develop personalized scientific growth programs.', 'AI YOU FOUNDATION', 'This activity, together with the Growth Angel Fund of China Red Cross Foundation, will jointly pay attention to the growth and development of young children, early detection of related diseases, and will provide special public welfare funds to provide help for eligible poor short children, hoping to help more children improve their quality of life and reduce the burden of their families through this public welfare activity. To promote the attention of the whole society to the growth and development of children.', 'With the improvement of people\'s living standards and the change of life style, the growth and development problems of children such as obesity, precocious puberty, growth and development delay have soared. At present, there are about 7 million short children in China, and the number of overweight and obese children over 7 years old is about 39 million, which has become a public health problem endangering children\'s health.', 0, 66000, '1', 'http://127.0.0.1:8081/TheCharity/images/hero.jpg', 'http://127.0.0.1:8081/TheCharity/images/event-2.jpg', '2022-04-14 15:34:22', 0);
INSERT INTO `causes` VALUES (6, 'Angel Hospital started the spring rehabilitation aid activity to help the poor children grow up healthily', 'In order to comprehensively help the development and growth of special children and alleviate the economic difficulties of families with special children, Angel Children\'s Hospital hereby conducts public welfare assistance activities for children with difficult diseases in the spring', 'CHILDREN\'S CHARITY', 'Angel Public welfare activities Carry out public welfare measures for children in distress between the ages of 2 and 14, and provide various measures such as \"public assistance and treatment\", assessment and examination, and free health clinic for families of poor children. Through medical assistance for early intervention and rehabilitation of children in distress, we care for the healthy physical and mental growth of young children, so that more children in poor areas can enjoy high-quality and convenient diagnosis and treatment services.', 'In recent years, Angel Children\'s Hospital has earnestly safeguarded and protected the basic rights and interests of children, increased the assistance and protection of children with special difficulties, and constantly improved the level of children\'s welfare work. With the theme of helping the healthy growth of young people, the Sunshine Youth Fund has helped some poor young families with growth subsidies, and established the project \"Tongkang 100 Dream Public Welfare Action\" to help more poor children and enable poor disabled children to receive rehabilitation assistance.', 0, 120000, '3', 'http://127.0.0.1:8081/TheCharity/images/cause-bg.jpg', 'http://127.0.0.1:8081/TheCharity/images/event-3.jpg', '2022-04-15 15:38:40', 0);
INSERT INTO `causes` VALUES (7, 'Easily raise money to start a care program for autistic children and protect children from the stars', 'Recently, the United Public Welfare Foundation (WABC) launched the \"Wrong Socks Day Special Children Care Program\" to send warmth and care to children, calling on everyone to wear wrong socks to express support for special people such as \"autism\" and \"cerebral palsy\".', 'Children\'s Foundation of China', 'In order to increase social inclusion and arouse public empathy, in 2016, the Shanghai Art Way Public Welfare Foundation (WABC) launched the \"Wear Two Different Socks\" public welfare behavior and designated December 23 as the \"Wrong Socks Day\" every year. Now, the \"Wrong Socks Day\" ushered in the sixth anniversary, on December 23 this year, easy funding and Shanghai Art Road Public Welfare Foundation (WABC), once again to focus on a group of children from the stars in this world.', 'At the same time, the easy funding also invited the popular anchor little crazy hand in hand to open the public interest broadcast room, will carry out the charity sale socks live activities, the charity sale will all be used for special children\'s public welfare help. Call on more people to pay attention to this kind of special children and give more love and understanding to their childhood.\r\nAccording to incomplete statistics, since the wrong socks Day began, volunteers from more than 10 countries have jointly supported the wrong socks public welfare action, and the wrong socks related topics have received nearly 200 million reads online, more than 60,000 interactions, and the influence of the wrong socks concept can reach tens of millions of people.', 0, 88000, '2', 'http://127.0.0.1:8081/TheCharity/images/5.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-4.jpg', '2022-04-18 01:16:21', 0);
INSERT INTO `causes` VALUES (8, '2022 \"Smile Action\" plan to help children with autism in Huizhou', 'The Chinese Children\'s Autism Charity Walk aims to guide the whole society to participate in caring for autistic children, and promote the integration of traditional Chinese and Western medicine in the field of children\'s autism charity relief projects. This activity is assisted by social charity funds, and takes \"scientific intervention and reasonable treatment\" as the core principle to help autistic children aged 3-6.', 'CHILDREN\'S CHARITY', 'The first stop of the \"Smile Action\" plan in Huizhou in 2022 raised 400,000 yuan for public welfare assistance for 4 children with autism in Huizhou. The four \"star children\" will receive eight days of modern traditional Chinese medicine pressure wave meridian conditioning treatment to achieve the purpose of improving core symptoms. In addition, the Huizhou Cihang Public Welfare Association plans to raise 2 million yuan this year to provide public welfare assistance to 20 autistic children in the Greater Bay Area, bringing new hope to autistic children and their families.', 'In 2021, the China Children\'s Autism Charity Miles has carried out five consecutive public welfare assistance activities for 30 autistic children in different regions, different ethnic groups and different ages (3 to 9 years old) in Jilin, Ningbo, Beijing, Lijiang and Huizhou.\r\n\r\nOn the afternoon of March 5, the China Children\'s Autism Charity Walk sent warmth to the grassroots, and jointly launched the Guangdong-Hong Kong-Macao Greater Bay Area to launch the \"Smile Action\" plan for children with autism. \"Smile Action\" aims to integrate the latest research results of modern traditional Chinese medicine meridian diagnosis and treatment in health medicine, and continue to bring public welfare actions of self-care, controllable behavior and integration into society to autistic children of appropriate age in the Guangdong-Hong Kong-Macao Greater Bay Area.', 0, 92000, '1', 'http://127.0.0.1:8081/TheCharity/images/7.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-5.jpg', '2022-04-10 01:19:40', 0);
INSERT INTO `causes` VALUES (9, 'Sky Star Public welfare - mobile book box', 'In order to meet the needs of rural teachers and students for the promotion of reading, the Star launched the \"mobile book box\" project', 'AI YOU FOUNDATION', 'In order to meet the needs of rural teachers and students for the promotion of reading, Full Sky Public Welfare launched the \"Mobile Book box\" project: with the appropriate conditions of central schools as branch libraries, together to form a mobile book resources network, through the voluntary application of rural teachers who are willing to promote reading, so that high-quality reading resources flow to the more scarce rural teachers and children. Compared with the high investment of a public library of 100,000 yuan, a mobile book box of 1,200 yuan balances the cost and demand, and meets the needs of rural reading resources as a part of the county reading promotion.', 'The mobile book box is the third library model in addition to the primary school library and the community library.\r\n\r\nSchools that lack resources often lack teachers to support the open operation of libraries, so donating libraries is not the best choice for such schools. The mobile book box project is based on the concept of \"shared reading and circular reading\", which selects beautiful picture books and children\'s books at home and abroad to be placed in the book box, which can greatly improve the utilization rate of books and enable children in areas with insufficient reading resources to enjoy high-quality children\'s book resources. It also allows teachers who are willing to carry out reading courses and activities to have more appropriate reading resources.', 0, 100000, '1', 'http://127.0.0.1:8081/TheCharity/images/8.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-6.jpg', '2022-04-12 01:22:08', 0);
INSERT INTO `causes` VALUES (10, 'Blood Disease Children Rescue Project - Aiyou Angel Application Way (Leukemia children rescue)', 'Aiyou Children\'s Medical Project Matrix focuses on the medical assistance of orphan and poor children, and has carried out cancer-related projects such as \"Aiyou Angel\" - medical assistance project for orphan and poor blood diseases and tumor children', 'Children\'s Foundation of China', 'Aiyou Children\'s Medical Project Matrix focuses on the medical assistance of orphan and poor children, and has carried out cancer-related projects such as \"Aiyou Angel\" - medical assistance project for orphan and poor blood diseases and tumor children (diseases other than leukemia need to consult the designated hospital and foundation for assistance, and the project application needs to be applied to partner hospitals).', 'Aiyou Charity Foundation is a 5A-level foundation assessed by the Ministry of Civil Affairs, initiated and managed by entrepreneurs, and carries out projects across the country. After more than ten years of exploration and development, Aiyou has formed three major project matrices: children\'s medical care, children\'s welfare and public welfare venture capital.', 0, 150000, '3', 'http://127.0.0.1:8081/TheCharity/images/9.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-7.jpg', '2022-04-13 01:23:52', 0);
INSERT INTO `causes` VALUES (11, 'Action to improve the lives of children in distress', 'The \"Action to Improve the Lives of Children in Difficult Situations\" launched by the Good Home Project of China Children\'s Fund (UNICEF) aims to provide orphans, orphans, left-behind children and other children in difficult situations in partner cities with comprehensive services in life, study, serious illness and accident due to their parents\' death, economic poverty, lack of food and clothing, and malnutrition, and to convey human care and warmth to children. Reproduce a bright smile, grow up happy and healthy.', 'AI YOU FOUNDATION', 'The \"Action to Improve the Lives of Children in Difficult Situations\" launched by the Good Home Project of China Children\'s Fund (UNICEF) aims to provide orphans, orphans, left-behind children and other children in difficult situations in partner cities with comprehensive services in life, study, serious illness and accident due to their parents\' death, economic poverty, lack of food and clothing, and malnutrition, and to convey human care and warmth to children.', '1. We will set up a fund custody account for each orphan and provide annual living and learning subsidies, so that children can enjoy the right to fair education, so that knowledge can change the fate of orphans and achieve their future;\r\n\r\n2, we provide orphans with serious diseases, medical and accidental injuries every year, to help them resist the invasion of major diseases and accidental injuries, to help orphans grow up healthily;\r\n\r\n3. Our volunteers visit orphans regularly, build a database of orphans, learn about their learning and life, pass on social care and human warmth, and help orphans grow up healthily and happily.', 0, 70000, '1', 'http://127.0.0.1:8081/TheCharity/images/10.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-8.jpg', '2022-04-07 01:25:40', 0);
INSERT INTO `causes` VALUES (12, 'Love you children\'s heart to help children with congenital heart disease courage to overcome the disease, open a new life', '\"Give life a chance, give children a future\" is the philosophy and mission of Aiyou Charity Foundation, and it is also the source of motivation for Aiyou to keep moving forward.', 'CHILDREN\'S CHARITY', 'The project adopts the cooperation mode of designated hospitals. Hospitals with good medical conditions are selected as designated units in all regions of the country to provide treatment for children with congenital heart disease. Actively cooperate with designated hospitals, donors, government departments and other social organizations to build a rescue network, realize the complementary and sharing of superior resources, give play to the efficient role of the rescue platform, create medical conditions for children, so that more children can enjoy high-quality medical examination and surgical treatment.', 'Congenital heart disease is a common congenital disease of newborns, as long as it is treated in time, most of the simple congenital heart disease can be completely treated, most of the complex congenital heart disease can also be treated, with postoperative rehabilitation assistance, basically can also protect the quality of life.', 0, 180000, '1', 'http://127.0.0.1:8081/TheCharity/images/11.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-9.jpg', '2022-04-08 01:27:36', 0);
INSERT INTO `causes` VALUES (13, 'Inclusive education support program for children', 'Beautiful Home will provide public welfare donations for the smart education cloud platform for partner cities, and help local education administration departments build localized education resources public service platform + education management public service platform.', 'Children\'s Foundation of China', 'In order to allow children in the partner cities to share the capital\'s high-quality education resources, strengthen quality training, achieve balanced education, and promote inclusive education, Beautiful Home will provide public welfare donations for the smart education cloud platform in the partner cities, and help the local education administration departments to build a localized education resources public service platform + education management public service platform.', 'In order to allow children in the partner cities to share the capital\'s high-quality education resources, strengthen quality training, achieve balanced education, and promote inclusive education, Beautiful Home will provide public welfare donations for the smart education cloud platform in the partner cities, and help the local education administration departments to build a localized education resources public service platform + education management public service platform. At the same time, according to the needs of the government, the curriculum system and quality education courseware (science, music, Go) adopted simultaneously by dozens of famous and excellent kindergartens such as experimental kindergartens under the direct administration of the Central government were introduced and donated free of charge. In order to ensure the effectiveness of public welfare activities, the project office will provide free teacher training for demonstration kindergartens.', 0, 96000, '1', 'http://127.0.0.1:8081/TheCharity/images/13.jpg', 'http://127.0.0.1:8081/TheCharity/images/u-10.jpg', '2022-04-09 01:30:05', 0);
INSERT INTO `causes` VALUES (14, 'The public welfare project \"Point Love · Micro Wish\" was officially launched', '\"Point Love Micro Wish\" is a public welfare project jointly established by Shandong Women and Children Development Foundation and Qilu Evening News Qilu One Point. Under the leadership and support of Shandong Women\'s Federation, the 16th Municipal Women\'s Federation and Qilu Evening News and Qilu 116:Financial Media Center jointly encouraged and guided various enterprises and social organizations to carry out charitable activities and social donations, and led all sectors of society to jointly pay attention to and care for children in difficulties, rural left-behind children and autistic children.', 'One hundred, Qilu', 'With the purpose of \"Gathering little love, casting hope for the future\", the public welfare project \"Little Love · Little Wish\" was launched in Jinan, caring for the growth of children in difficulties and rural left-behind children, and supporting and participating in the rehabilitation and relief of autistic children.\r\nEncourage and guide various enterprises and social organizations to carry out charitable activities and social donations, and encourage all sectors of society to jointly pay attention to and care for children in difficulties, rural left-behind children and autistic children.', '\"Dot Love micro Wish\" public welfare projects should meet the wealth of spiritual life while achieving material prosperity. In terms of material life care, prepare daily necessities wish kits, school supplies wish kits or sports goods wish kits for the children; In terms of spiritual life care, social organizations, Qilu Evening News and Qilu One Point volunteer team actively carry out care services, and cultivate children\'s self-improvement and optimistic attitude towards life.\r\n\r\nAt the same time, we pay attention to showing children\'s spiritual outlook of self-improvement and growth under the warmth of the party, educate children to constantly establish national consciousness, deepen patriotism, stimulate the patriotic struggle to love the Party, and strive to be a socialist builder and reliable successor.\r\n\r\nIn June 2021, Shandong Women\'s and Children\'s Development Foundation reached a strategic cooperation with Qilu Evening News · Qilu One Point to jointly develop public welfare undertakings based on sustainable development. Jointly create the \"Dot Love Action\" public welfare brand, set up the \"Dot love · micro wish\" public welfare project, aiming at gathering little love and casting hope for the future.\r\n\r\nSince its establishment in 1981, Shandong Women\'s and Children\'s Development Foundation has been actively promoting and mobilizing social forces to raise funds and develop women\'s and children\'s causes. Under the leadership of Shandong Provincial Women\'s Federation, the sixth Council actively carried out women\'s and children\'s charity work and made breakthroughs in self-construction and fundraising scale.', 0, 80000, '1', 'http://localhost:8081/TheCharity/images/F81A7B4EE62A4FB1AA5073280B8417EE_pro01.jpg', 'http://localhost:8081/TheCharity/images/074063F452A142F6B7438257C3D31136_p01.jpg', '2022-04-24 16:39:55', 0);

-- ----------------------------
-- Table structure for comments
-- ----------------------------
DROP TABLE IF EXISTS `comments`;
CREATE TABLE `comments`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '评论id',
  `news_id` bigint(20) NOT NULL COMMENT '评论的资讯id',
  `root_id` bigint(20) NULL DEFAULT -1 COMMENT '根评论id',
  `content` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '评论内容',
  `to_comment_user_id` bigint(20) NULL DEFAULT -1 COMMENT '所回复评论的userid',
  `to_comment_id` bigint(20) NULL DEFAULT -1 COMMENT '所回复的评论id',
  `create_time` datetime NULL DEFAULT NULL COMMENT '评论时间',
  `create_by` bigint(20) NULL DEFAULT NULL COMMENT '评论发起的用户id',
  `del_flag` int(11) NULL DEFAULT 0 COMMENT '删除标志(0表示未删除1表示删除)',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 15 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '资讯评论表' ROW_FORMAT = Dynamic;

