-- ----------------------------
-- Table structure for donations
-- ----------------------------
DROP TABLE IF EXISTS `donations`;
CREATE TABLE `donations`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '捐助订单id',
  `trade_no` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '交易订单号',
  `amount` decimal(10, 2) NOT NULL COMMENT '捐助金额',
  `user_id` bigint(20) NOT NULL COMMENT '捐助者id',
  `message` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '用户没留言，不过他们留下了爱心' COMMENT '捐助者留言',
  `cause_id` bigint(20) NOT NULL COMMENT '捐助项目id',
  `create_time` datetime NULL DEFAULT NULL COMMENT '捐助时间',
  `status` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0' COMMENT '0为未支付1为已支付',
  `del_flag` int(11) NULL DEFAULT 0 COMMENT '删除标志(0表示未删除1表示已删除)',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 19 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '捐助情况表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of donations
-- ----------------------------
