-- ----------------------------
-- Table structure for skill
-- ----------------------------
DROP TABLE IF EXISTS `skill`;
CREATE TABLE `skill`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '专业技能id',
  `skill_name` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '专业技能名',
  `description` varchar(2000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '技能描述',
  `del_flag` int(11) NULL DEFAULT 0 COMMENT '删除标志(0表示未删除1表示删除)',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `skill_skill_name_uindex`(`skill_name`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 13 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '志愿者专业技能表' ROW_FORMAT = Dynamic;

